    		where m_id = #{id} and m_passwd = #{passwd}


-- select * from members where m_email = '카카오 로그인 이메일';
   select *
   		from members
   			where m_email = #{email}

		select * 
    	from members
    	where m_name = #{m_name} and m_tel = #{m_tel}

		select * 
    	from CEO
    	where c_name = #{c_name} and c_tel = #{c_tel}

		select * 
    	from members
    	where m_id = #{m_id} and m_name = #{m_name} and m_tel = #{m_tel}

		select * 
    	from CEO
    	where c_id = #{c_id} and c_name = #{c_name} and c_tel = #{c_tel}
