mapper.insert:


	SELECT c_idx FROM ceo 
		WHERE c_id = #{c_id}


INSERT 
	INTO restaurant
	VALUES (
		null -- idx 자동증가
		, #{res_brn}
		, #{res_name}
		, #{res_tel}
		, #{res_address}
		, #{res_detailAddress}
		, CONCAT(#{res_open}, '-' , #{res_close}) -- res_openinghours 문자열 결합 
		, CONCAT(#{res_breakstart}, '-' , #{res_breakend}) -- res_breaktime 문자열 결합 
		, #{res_total_table}
		, #{res_dayoff}
		, #{res_amenity}
		, #{res_amenity_info}
		, #{res_photo1} -- 실제파일명
		, #{res_photo2}
		, #{res_photo3}
		, #{res_intro}
		, #{c_idx}
	)

mapper.update:
UPDATE restaurant
	SET
		res_brn = #{res_brn}
		, res_name = #{res_name}
		, res_tel = #{res_tel}
		, res_address = #{res_address}
		, res_detailAddress = #{res_detailAddress}
		, res_openinghours = CONCAT(#{res_open}, '-' , #{res_close}) -- res_openinghours 문자열 결합 
		, res_breaktime = CONCAT(#{res_breakstart}, '-' , #{res_breakend}) -- res_breaktime 문자열 결합 
		, res_total_table = #{res_total_table}
		, res_dayoff = #{res_dayoff}
		, res_amenity = #{res_amenity}
		, res_amenity_info = #{res_amenity_info}

			, res_photo1 = #{res_photo1}


			, res_photo2 = #{res_photo2}


			, res_photo3 = #{res_photo3}

		, res_intro = #{res_intro}
			WHERE
				res_idx = #{res_idx}

UPDATE restaurant
	SET res_brn = #{res_brn}
	   ,res_name = #{res_name}
	   ,res_tel = #{res_tel}
	   ,res_address = SUBSTRING_INDEX(#{restaurantAdress}, ',' ,1)
	   ,res_detailAddress = SUBSTRING_INDEX(#{restaurantAdress}, ',' , -1)
	   ,c_idx = #{c_idx}
		WHERE res_idx = #{res_idx}
