mapper.insert:

	SELECT IFNULL(MAX(no_idx), 0) FROM notice

INSERT INTO notice VALUES (
	NULL
	, #{no_subject}
	, #{no_content}
	, #{no_category}
	, now()
	
		
		, #{no_file}
		
		
		, null
		
	
	, 0
)

mapper.update:
UPDATE notice
	SET 
		no_subject = #{no_subject}
		, no_content = #{no_content}
		
			, no_file = #{no_file}
		
	WHERE 
