mapper.insert:
INSERT 
	INTO ceo
	VALUES (
		NULL
		, #{c_id}
		, #{c_passwd} 
		, #{c_name}
		, #{c_tel}
		, #{c_email}
		, 1
		, now()
	)

mapper.update:
UPDATE CEO
	SET c_passwd = #{c_passwd}
	WHERE c_id = #{c_id} AND c_name = #{c_name}

mapper.select:
select c_passwd
	from ceo
		where c_id = #{id}
