mapper.insert:
INSERT 
	INTO restaurant
	VALUES (
		null -- idx 자동증가
		, #{res_brn}
		, #{res_name}
		, #{res_tel}
		, #{res_postcode}
		, #{res_address}
		, #{res_detailAddress}
		, #{res_open}
		, #{res_close}
		, #{res_breakstart}
		, #{res_breakend}
		, #{res_holiday}
		, #{res_amenity}
		, #{res_photo}
		, #{res_intro}

	)

mapper.update:
UPDATE restaurant
	SET
		res_brn = #{res_brn}
		, res_name = #{res_name}
		, res_tel = #{res_tel}
		, res_postcode = #{res_postcode}
		, res_address = #{res_address}
		, res_detailAddress = #{res_detailAddress}
		, res_open = #{res_open}
		, res_close = #{res_close}
		, res_breakstart = #{res_breakstart}
		, res_breakend = #{res_breakend}
		, res_holiday = #{res_holiday}
		, res_amenity = #{res_amenity}
		, res_photo = #{res_photo}
		, res_intro = #{res_intro}
			WHERE
				res_brn = #{res_brn}
